import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { useAuth } from '../context/AuthContext';

const DashboardPage = () => {
    const { currentUser } = useAuth();
    const [activeReport, setActiveReport] = useState('user-logs');
    const [userLogs, setUserLogs] = useState([]);
    const [inventoryData, setInventoryData] = useState([]);
    const [loading, setLoading] = useState(false);
    const [dateRange, setDateRange] = useState({
        startDate: '',
        endDate: ''
    });    // Fetch User Logs Data
    const fetchUserLogs = async () => {
        setLoading(true);
        try {
            const response = await fetch('http://localhost:3001/api/admin/user-logs-test', {
                headers: {
                    'Content-Type': 'application/json'
                }
            });
            if (response.ok) {
                const data = await response.json();
                setUserLogs(data);
                console.log('User logs loaded:', data.length, 'users');
            }
        } catch (error) {
            console.error('Error fetching user logs:', error);
        } finally {
            setLoading(false);
        }
    };

    // Fetch Inventory Data
    const fetchInventoryData = async () => {
        setLoading(true);
        try {
            const response = await fetch('http://localhost:3001/api/admin/inventory-report', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                },
                body: JSON.stringify(dateRange)
            });
            if (response.ok) {
                const data = await response.json();
                setInventoryData(data);
            }
        } catch (error) {
            console.error('Error fetching inventory data:', error);
        } finally {
            setLoading(false);
        }
    };

    // Generate Reports
    const generateReport = () => {
        if (activeReport === 'user-logs') {
            fetchUserLogs();
        } else if (activeReport === 'inventory') {
            fetchInventoryData();
        }
    };    // Format Date
    const formatDate = (dateString) => {
        if (!dateString) return 'Never';
        return new Date(dateString).toLocaleString();
    };

    // Auto-load user logs when component mounts
    useEffect(() => {
        if (activeReport === 'user-logs') {
            fetchUserLogs();
        }
    }, [activeReport]);

    // Check if user is admin
    if (!currentUser || currentUser.role !== 'admin') {
        return (
            <Container>
                <UnauthorizedMessage>
                    <h2>Unauthorized Access</h2>
                    <p>You need admin privileges to access this dashboard.</p>
                </UnauthorizedMessage>
            </Container>
        );
    }

    return (
        <Container>
            <Header>
                <Title>Dashboard</Title>
                <Subtitle>Administrative Reports & Analytics</Subtitle>
            </Header>

            <ReportsContainer>
                <SidePanel>
                    <SectionTitle>Reports</SectionTitle>
                    <ReportNavigation>
                        <ReportButton 
                            $active={activeReport === 'user-logs'}
                            onClick={() => setActiveReport('user-logs')}
                        >
                            <ReportIcon>📊</ReportIcon>
                            User Logs Report
                        </ReportButton>
                        
                        <ReportButton 
                            $active={activeReport === 'inventory'}
                            onClick={() => setActiveReport('inventory')}
                        >
                            <ReportIcon>📦</ReportIcon>
                            Inventory Report
                        </ReportButton>
                        
                        <ReportButton 
                            $active={activeReport === 'delivery'}
                            onClick={() => setActiveReport('delivery')}
                        >
                            <ReportIcon>🚚</ReportIcon>
                            Delivery Completion Report
                            <ComingSoon>Coming Soon</ComingSoon>
                        </ReportButton>
                        
                        <ReportButton 
                            $active={activeReport === 'sales'}
                            onClick={() => setActiveReport('sales')}
                        >
                            <ReportIcon>💰</ReportIcon>
                            Sales Report
                            <ComingSoon>Coming Soon</ComingSoon>
                        </ReportButton>
                    </ReportNavigation>
                </SidePanel>

                <MainContent>
                    {/* User Logs Report */}
                    {activeReport === 'user-logs' && (
                        <ReportSection>
                            <ReportHeader>
                                <ReportTitle>User Logs Report</ReportTitle>
                                <GenerateButton onClick={generateReport} disabled={loading}>
                                    {loading ? 'Generating...' : 'Generate Report'}
                                </GenerateButton>
                            </ReportHeader>
                            
                            <ReportDescription>
                                View comprehensive user activity logs including login times, account creation, and updates.
                            </ReportDescription>

                            {userLogs.length > 0 && (
                                <TableContainer>
                                    <Table>
                                        <TableHeader>
                                            <tr>
                                                <th>User ID</th>
                                                <th>Username</th>
                                                <th>Email</th>
                                                <th>Role</th>
                                                <th>Last Login</th>
                                                <th>Account Created</th>
                                                <th>Last Updated</th>
                                                <th>Status</th>
                                            </tr>
                                        </TableHeader>
                                        <TableBody>
                                            {userLogs.map(user => (
                                                <TableRow key={user.id}>
                                                    <td>{user.id}</td>
                                                    <td>{user.username}</td>
                                                    <td>{user.email}</td>
                                                    <td>
                                                        <RoleBadge $role={user.role}>
                                                            {user.role}
                                                        </RoleBadge>
                                                    </td>
                                                    <td>{formatDate(user.last_login)}</td>
                                                    <td>{formatDate(user.created_at)}</td>
                                                    <td>{formatDate(user.updated_at)}</td>
                                                    <td>
                                                        <StatusBadge $status={user.status ? 'active' : 'inactive'}>
                                                            {user.status ? 'active' : 'inactive'}
                                                        </StatusBadge>
                                                    </td>
                                                </TableRow>
                                            ))}
                                        </TableBody>
                                    </Table>
                                </TableContainer>
                            )}
                        </ReportSection>
                    )}

                    {/* Inventory Report */}
                    {activeReport === 'inventory' && (
                        <ReportSection>
                            <ReportHeader>
                                <ReportTitle>Inventory Report</ReportTitle>
                                <DateRangeContainer>
                                    <DateInput
                                        type="date"
                                        value={dateRange.startDate}
                                        onChange={(e) => setDateRange({...dateRange, startDate: e.target.value})}
                                        placeholder="Start Date"
                                    />
                                    <DateInput
                                        type="date"
                                        value={dateRange.endDate}
                                        onChange={(e) => setDateRange({...dateRange, endDate: e.target.value})}
                                        placeholder="End Date"
                                    />
                                    <GenerateButton onClick={generateReport} disabled={loading}>
                                        {loading ? 'Generating...' : 'Generate Report'}
                                    </GenerateButton>
                                </DateRangeContainer>
                            </ReportHeader>
                            
                            <ReportDescription>
                                Generate comprehensive inventory reports with product status and date range filtering.
                            </ReportDescription>

                            {inventoryData.length > 0 && (
                                <TableContainer>
                                    <Table>
                                        <TableHeader>
                                            <tr>
                                                <th>Product ID</th>
                                                <th>Product Name</th>
                                                <th>Price</th>
                                                <th>Stock</th>
                                                <th>Status</th>
                                                <th>Created Date</th>
                                                <th>Last Updated</th>
                                                <th>Category</th>
                                            </tr>
                                        </TableHeader>
                                        <TableBody>
                                            {inventoryData.map(product => (
                                                <TableRow key={product.id}>
                                                    <td>{product.product_id}</td>
                                                    <td>{product.productname}</td>
                                                    <td>₱{product.productprice}</td>
                                                    <td>{product.total_stock || 0}</td>
                                                    <td>
                                                        <StatusBadge $status={product.productstatus}>
                                                            {product.productstatus}
                                                        </StatusBadge>
                                                    </td>
                                                    <td>{formatDate(product.created_at)}</td>
                                                    <td>{formatDate(product.updated_at)}</td>
                                                    <td>{product.productcolor || 'N/A'}</td>
                                                </TableRow>
                                            ))}
                                        </TableBody>
                                    </Table>
                                </TableContainer>
                            )}
                        </ReportSection>
                    )}

                    {/* Delivery Completion Report Template */}
                    {activeReport === 'delivery' && (
                        <ReportSection>
                            <ReportHeader>
                                <ReportTitle>Delivery Completion Report</ReportTitle>
                            </ReportHeader>
                            
                            <ComingSoonSection>
                                <ComingSoonIcon>🚧</ComingSoonIcon>
                                <ComingSoonTitle>Coming Soon</ComingSoonTitle>
                                <ComingSoonText>
                                    This report will provide comprehensive delivery completion analytics including:
                                </ComingSoonText>
                                <FeatureList>
                                    <li>Delivery success rates</li>
                                    <li>Average delivery times</li>
                                    <li>Delivery status tracking</li>
                                    <li>Geographic delivery analysis</li>
                                    <li>Performance metrics</li>
                                </FeatureList>
                            </ComingSoonSection>
                        </ReportSection>
                    )}

                    {/* Sales Report Template */}
                    {activeReport === 'sales' && (
                        <ReportSection>
                            <ReportHeader>
                                <ReportTitle>Sales Report</ReportTitle>
                            </ReportHeader>
                            
                            <ComingSoonSection>
                                <ComingSoonIcon>🚧</ComingSoonIcon>
                                <ComingSoonTitle>Coming Soon</ComingSoonTitle>
                                <ComingSoonText>
                                    This report will provide detailed sales analytics including:
                                </ComingSoonText>
                                <FeatureList>
                                    <li>Revenue analysis by time period</li>
                                    <li>Top-selling products</li>
                                    <li>Customer purchase patterns</li>
                                    <li>Profit margin calculations</li>
                                    <li>Sales performance trends</li>
                                </FeatureList>
                            </ComingSoonSection>
                        </ReportSection>
                    )}
                </MainContent>
            </ReportsContainer>
        </Container>
    );
};

// Styled Components
